mapper.select:
SELECT post_id, title, to_char(created_at, 'YYYY-MM-DD"T"HH24:MI:SS') as created_at, category_id, price, thumbnail, user_id, user_name FROM ( SELECT /*+ INDEX_DESC(FLEAMARKET SYS_C0023964) */ f.post_id, f.title, f.created_at, f.category_id, f.price, f.thumbnail, f.user_id , m.user_name, ROW_NUMBER() OVER (ORDER BY f.post_id DESC) AS rnum FROM fleaMarket f JOIN member m ON f.user_id = m.user_id
f.sales_status = 0
AND f.title LIKE '%' || #{keyword} || '%'
AND f.category_id IN ( SELECT category_id FROM flea_category WHERE category_id = #{category_id} OR parent_id = #{category_id} )
)
WHERE rnum > (#{pageNum} - 1) * #{amount} AND rnum <= #{pageNum} * #{amount}

SELECT f.post_id, f.title, to_char(f.created_at, 'YYYY-MM-DD"T"HH24:MI:SS') as created_at, f.category_id, f.price, f.thumbnail, f.user_id, f.user_name, CASE WHEN fav.board_no IS NOT NULL THEN 1 ELSE 0 END AS isFavorite FROM ( SELECT /*+ INDEX_DESC(FLEAMARKET SYS_C0023964) */ f.post_id, f.title, f.created_at, f.category_id, f.price, f.thumbnail, f.user_id, m.user_name, ROW_NUMBER() OVER (ORDER BY f.post_id DESC) AS rnum FROM fleaMarket f JOIN member m ON f.user_id = m.user_id
f.sales_status = 0
AND f.title LIKE '%' || #{keyword} || '%'
AND f.category_id IN ( SELECT category_id FROM flea_category WHERE category_id = #{category_id} OR parent_id = #{category_id} )
) f LEFT JOIN favorite fav ON f.post_id = fav.board_no AND fav.board_type = 'fleaMarket' AND fav.user_id = #{user_id, jdbcType=VARCHAR} WHERE rnum > (#{pageNum} - 1) * #{amount} AND rnum <= #{pageNum} * #{amount}

SELECT count(*)
FROM fleaMarket f
LEFT JOIN member m ON f.user_id = m.user_id


		f.title LIKE '%' || #{keyword} || '%'


		f.category_id IN (
		SELECT category_id
		FROM flea_category
		WHERE category_id = #{category_id}
		OR parent_id = #{category_id}
		)

mapper.update:
UPDATE fleaMarket
SET view_count = view_count + 1
WHERE post_id = #{post_id}

