mapper.select:

    SELECT post_id, title, thumbnail,
    TO_CHAR(created_at,'YYYY-MM-DD"T"HH24:MI:SS') AS created_at, 
    price, sales_status
    FROM (
        SELECT
            f.post_id, f.title, f.thumbnail, f.created_at, f.price, f.sales_status,
            ROW_NUMBER() OVER (ORDER BY f.post_id DESC) AS rnum
        FROM fleaMarket f
        WHERE f.user_id = #{user_id}


		AND f.title LIKE '%' || #{keyword} || '%'


    )
    WHERE rnum > (#{pageNum} - 1) * #{amount}
      AND rnum <= #{pageNum} * #{amount}

SELECT * FROM ( SELECT f.post_id, f.title, f.price, f.thumbnail, f.user_id, TO_CHAR(f.created_at,'YYYY-MM-DD"T"HH24:MI:SS') AS created_at, ROW_NUMBER() OVER (ORDER BY f.post_id DESC) AS rnum FROM fleaMarket f JOIN favorite fav ON f.post_id = fav.board_no WHERE fav.user_id = #{user_id} AND fav.board_type = 'fleaMarket'
AND f.title LIKE '%' || #{keyword} || '%'
) WHERE rnum > (#{pageNum} - 1) * #{amount} AND rnum <= #{pageNum} * #{amount}
