SELECT a.application_id, a.title AS application_title, TO_CHAR(a.created_at,'YYYY-MM-DD"T"HH24:MI:SS') AS created_at, a.post_id, j.title AS job_title, j.category, j.status, m.company_name FROM application a JOIN job j ON a.post_id = j.post_id LEFT JOIN member m ON m.user_id = j.user_id WHERE a.user_id = #{user_id}
AND a.created_at >= SYSDATE - #{periodDays}
AND j.status = #{job_status}
AND ( UPPER(j.title) LIKE UPPER(#{keyword}) ESCAPE '\' OR UPPER(j.content) LIKE UPPER(#{keyword}) ESCAPE '\' OR UPPER(m.company_name) LIKE UPPER(#{keyword}) ESCAPE '\' )
ORDER BY a.created_at DESC



SELECT COUNT(*)
FROM application a
WHERE a.user_id = #{user_id}



		AND a.created_at >= SYSDATE - #{periodDays}




	AND EXISTS (
		SELECT 1
		FROM job j
		WHERE j.post_id = a.post_id
		  AND j.status = #{}
	)


	AND (
		UPPER(j.title) LIKE UPPER(#{keyword}) ESCAPE '\'
		OR UPPER(j.content) LIKE UPPER(#{keyword}) ESCAPE '\'
		OR UPPER(m.company_name) LIKE UPPER(#{keyword}) ESCAPE '\'
	)

mapper.delete:
DELETE 
FROM application
