mapper.select:
SELECT j.post_id, j.title AS job_title, TO_CHAR(j.created_at,'YYYY-MM-DD"T"HH24:MI:SS') AS created_at, j.category, j.status, j.city, j.gu, j.dong, j.position, j.pay_category, j.pay, m.company_name FROM job j LEFT JOIN member m ON m.user_id = j.user_id WHERE EXISTS ( SELECT 1 FROM favorite f WHERE f.post_id = j.post_id AND f.board_type = 'job' AND f.user_id = #{user_id, jdbcType=VARCHAR} ) AND j.category = #{category}
AND j.created_at >= SYSDATE - #{periodDays}
AND j.status = #{status}
AND ( UPPER(j.title) LIKE UPPER(#{keyword}) ESCAPE '\' OR UPPER(j.content) LIKE UPPER(#{keyword}) ESCAPE '\'
OR UPPER(m.company_name) LIKE UPPER(#{keyword}) ESCAPE '\'
)
ORDER BY j.post_id DESC OFFSET (#{pageNum} - 1) * #{amount} ROWS FETCH NEXT #{amount} ROWS ONLY



SELECT
	COUNT(*)
FROM job j
LEFT JOIN member m
	ON m.user_id = j.user_id
WHERE EXISTS (
	SELECT 1
	FROM favorite f
	WHERE f.post_id = j.post_id
		AND f.board_type = 'job'
		AND f.user_id = #{user_id, jdbcType=VARCHAR}
)
AND j.category = #{category}

	AND j.created_at >= SYSDATE - #{periodDays}



	AND j.status = #{status}



	AND (
		UPPER(j.title) LIKE UPPER(#{keyword}) ESCAPE '\'
		OR UPPER(j.content) LIKE UPPER(#{keyword}) ESCAPE '\'

			OR UPPER(m.company_name) LIKE UPPER(#{keyword}) ESCAPE '\'

	)
