mapper.select:

SELECT roomRental_no AS board_no, roomRental_title AS board_title, created_at
FROM (
    SELECT 
       	r.roomRental_no, r.roomRental_title, r.roomRental_date,
        ROW_NUMBER() OVER (ORDER BY r.roomRental_no DESC) AS rnum
    FROM roomRental r
    WHERE r.roomRental_no IN (
        SELECT /*+ INDEX(FAVORITE UNIQUE_FAVORITE) */
            post_id 
        FROM favorite 
        WHERE user_id = #{user_id} 
        AND board_type = 'ROOM'
    )
)
WHERE rnum > (#{pageNum} - 1) * #{amount} 
AND rnum <= #{pageNum} * #{amount}

mapper.delete:
DELETE 
FROM favorite
WHERE user_id = #{user_id} 
	AND board_type=#{board_type} 
	AND post_id = #{post_id}

mapper.select:
SELECT COUNT(*)
FROM favorite
WHERE user_id = #{user_id}
	AND board_type = #{board_type}

SELECT user_id, user_name, address, phone, social_login
FROM member
WHERE user_id = #{user_id}
